import React, { useState } from 'react';
import lamparas2 from '../image/lamparas2.jpg';

const proyectos = [
  {
    id: 1,
    imagen: lamparas2,
    nombre: 'Proyecto 1',
    tecnologias: ['React', 'Tailwind CSS', 'Firebase'],
    descripcion:
      'Descripción del proyecto 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
  },
  {
    id: 2,
    imagen: lamparas2,
    nombre: 'Proyecto 2',
    tecnologias: ['Node.js', 'Express', 'MongoDB'],
    descripcion:
      'Descripción del proyecto 2. Ut enim ad minim veniam, quis nostrud exercitation ullamco.',
  },
  {
    id: 3,
    imagen: lamparas2,
    nombre: 'Proyecto 3',
    tecnologias: ['Python', 'Django', 'PostgreSQL'],
    descripcion:
      'Descripción del proyecto 3. Duis aute irure dolor in reprehenderit in voluptate.',
  },
  {
    id: 4,
    imagen: lamparas2,
    nombre: 'Proyecto 4',
    tecnologias: ['React Native', 'Expo', 'GraphQL'],
    descripcion:
      'Descripción del proyecto 4. Excepteur sint occaecat cupidatat non proident.',
  },
  {
    id: 5,
    imagen: lamparas2,
    nombre: 'Proyecto 5',
    tecnologias: ['Angular', 'Firebase', 'Firestore'],
    descripcion:
      'Descripción del proyecto 5. Nemo enim ipsam voluptatem quia voluptas sit aspernatur.',
  },
  {
    id: 6,
    imagen: lamparas2,
    nombre: 'Proyecto 6',
    tecnologias: ['Vue.js', 'Nuxt.js', 'MongoDB'],
    descripcion:
      'Descripción del proyecto 6. Tempor incididunt ut labore et dolore magna aliqua.',
  },
];

const Proyectos = ({ seccionAnimacion }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 3;

  // Calculando índices de proyectos para la página actual
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = proyectos.slice(indexOfFirstProject, indexOfLastProject);

  // Cambiar página
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div
      id="Proyectos"
      className={`h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black ${
        seccionAnimacion === 'Proyectos' ? 'opacity-100 transform translate-y-0' : 'opacity-0 transform translate-y-full'
      }`}
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <h2 className="text-4xl font-bold">Proyectos</h2>
      <div className="mt-8 grid grid-cols-1 gap-8 md:grid-cols-3">
        {currentProjects.map((proyecto) => (
          <div key={proyecto.id} className="bg-white p-4 rounded-lg shadow-md">
            <img src={proyecto.imagen} alt={proyecto.nombre} className="w-full h-40 object-cover rounded-md mb-4" />
            <h3 className="text-lg font-semibold">{proyecto.nombre}</h3>
            <div className="flex flex-wrap gap-2 mt-2">
              {proyecto.tecnologias.map((tecnologia, index) => (
                <span
                  key={index}
                  className="px-2 py-1 bg-gray-200 text-gray-800 rounded-md text-sm"
                >
                  {tecnologia}
                </span>
              ))}
            </div>
            <p className="text-sm mt-2">{proyecto.descripcion}</p>
          </div>
        ))}
      </div>
      {/* Paginación */}
      <div className="mt-8">
        <ul className="flex space-x-2">
          {Array.from({ length: Math.ceil(proyectos.length / projectsPerPage) }).map((_, index) => (
            <li
              key={index}
              onClick={() => paginate(index + 1)}
              className={`cursor-pointer px-3 py-1 rounded-md ${
                currentPage === index + 1 ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-800'
              }`}
            >
              {index + 1}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Proyectos;
