import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';

function EfectoEscritura() {
  const [escribir, setEscribir] = useState('');
  const [mostrarTexto, setMostrarTexto] = useState(true); // Estado para controlar la visibilidad del texto
  const nombre = "Carmelo Rodríguez | Portafolio";

  useEffect(() => {
    let intervalo;
    let timeout;

    const escribirNombre = () => {
      let i = 0;
      intervalo = setInterval(() => {
        if (i < nombre.length) {
          setEscribir(nombre.substring(0, i + 1));
          i++;
        } else {
          clearInterval(intervalo);
          timeout = setTimeout(() => {
            setMostrarTexto(false); // Después de escribir, ocultamos el texto
          }, 300); // Tiempo para que el nombre desaparezca lentamente
        }
      }, 50); // Velocidad de escritura del nombre
    };

    escribirNombre();

    return () => {
      clearInterval(intervalo);
      clearTimeout(timeout);
    };
  }, []);

  return (
    <Transition
      show={mostrarTexto} // Mostrar el texto cuando mostrarTexto sea true
      enter="transition-opacity duration-1000"
      enterFrom="opacity-0"
      enterTo="opacity-100"
      leave="transition-opacity duration-1000"
      leaveFrom="opacity-100"
      leaveTo="opacity-0"
    >
      {/* Incluimos el contenido dentro de la Transition */}
      <h2 className="text-5xl font-normal text-white">
        <span className='font-times font-extrabold p-2 border-4 border-white'>
          {escribir}
        </span>
      </h2>
    </Transition>
  );
}

export default EfectoEscritura;
