import React from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import reactIcon from '../image/react.png';
import nodejsIcon from '../image/nodejs.png';

const DetalleProyecto = ({ proyecto }) => {
  const getIcon = (tecnologia) => {
    switch (tecnologia.toLowerCase()) {
      case 'react':
        return reactIcon;
      case 'node.js':
        return nodejsIcon;
      default:
        return null;
    }
  };

  return (
    <div className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-white to-black">
      <div
        className="bg-white p-8 rounded-lg shadow-md"
        style={{
          maxWidth: '800px',
          margin: 'auto',
        }}
      >
        <img src={proyecto.imagen} alt={proyecto.nombre} className="w-full h-60 object-cover rounded-md mb-4" />
        <h2 className="text-2xl font-bold mb-2">{proyecto.nombre}</h2>
        <div className="flex flex-wrap gap-2 mt-3 justify-center">
          {proyecto.tecnologias.map((tecnologia, index) => (
            <span
              key={index}
              className="flex items-center px-2 py-1 bg-black text-white rounded-md text-sm"
              style={{ fontSize: '0.9rem' }}
            >
              {getIcon(tecnologia) && (
                <img src={getIcon(tecnologia)} alt={tecnologia} className="w-6 h-6 mr-2" />
              )}
              {tecnologia}
            </span>
          ))}
        </div>
        <p className="text-sm mt-3">{proyecto.descripcion}</p>
        <a
          href={proyecto.enlace}
          target="_blank"
          rel="noopener noreferrer"
          className="mt-4 inline-block bg-black text-white px-4 py-2 rounded-md hover:bg-gray-800"
        >
          Ver más detalles
        </a>
      </div>
    </div>
  );
};

export default DetalleProyecto;
