import React, { useEffect } from 'react';
import { Transition } from '@headlessui/react';

function Slider() {
  const [currentSection, setCurrentSection] = React.useState('Inicio');

  const navegacion = (seccion) => {
    const element = document.getElementById(seccion);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setCurrentSection(seccion);
    }
  };

  // Efecto para actualizar la sección actual cuando se cambia de sección
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const sections = ['Inicio', 'Tecnologias', 'ExperienciayFormacion', 'Proyectos'];
      const currentSection = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= scrollPosition + 100; // Ajusta el valor según sea necesario
        }
        return false;
      });
      if (currentSection) {
        setCurrentSection(currentSection);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="relative">
      <nav className="fixed top-0 left-0 right-0 flex justify-center items-center py-4 z-50 bg-white">
        <div className="flex justify-center items-center rounded-full shadow-md bg-white">
          <button onClick={() => navegacion('Inicio')} className={`py-2 px-4 focus:outline-none rounded-full ${currentSection === 'Inicio' ? 'bg-gray-200 text-gray-800' : 'hover:bg-gray-200 hover:text-gray-800'}`}>
            Inicio
          </button>
          <button onClick={() => navegacion('Tecnologias')} className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${currentSection === 'Tecnologias' ? 'bg-gray-200 text-gray-800' : 'hover:bg-gray-200 hover:text-gray-800'}`}>
            Tecnologías
          </button>
          <button onClick={() => navegacion('ExperienciayFormacion')} className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${currentSection === 'ExperienciayFormacion' ? 'bg-gray-200 text-gray-800' : 'hover:bg-gray-200 hover:text-gray-800'}`}>
            Experiencia y Formación
          </button>
          <button onClick={() => navegacion('Proyectos')} className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${currentSection === 'Proyectos' ? 'bg-gray-200 text-gray-800' : 'hover:bg-gray-200 hover:text-gray-800'}`}>
            Proyectos
          </button>
        </div>
      </nav>

      <div id="Inicio" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-green-200 to-blue-200">
        <h2 className="text-4xl font-bold">Inicio</h2>
        <p className="mt-2">Scroll Down for next slide</p>
      </div>
      <div id="Tecnologias" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white">
        <h2 className="text-4xl font-bold">Tecnologías</h2>
        <p className="mt-2">Scroll Down for next slide</p>
      </div>
      <div id="ExperienciayFormacion" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white">
        <h2 className="text-4xl font-bold">Experiencia y Formación</h2>
        <p className="mt-2">Scroll Down</p>
      </div>
      <div id="Proyectos" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black">
        <h2 className="text-4xl font-bold">Proyectos</h2>
      </div>
    </div>
  );
}

export default Slider;
