import { Transition } from '@headlessui/react';
import React from 'react';
import lamparas2 from '../image/lamparas2.jpg'; // Ejemplo de imagen de fondo

function Proyectos({ seccionAnimacion }) {
  // Ejemplo de datos de proyectos
  const proyectos = [
    { id: 1, imagen: lamparas2, titulo: 'Proyecto 1' },
    { id: 2, imagen: lamparas2, titulo: 'Proyecto 2' },
    { id: 3, imagen: lamparas2, titulo: 'Proyecto 3' },
    { id: 4, imagen: lamparas2, titulo: 'Proyecto 4' },
    { id: 5, imagen: lamparas2, titulo: 'Proyecto 5' },
    { id: 6, imagen: lamparas2, titulo: 'Proyecto 6' },
    { id: 7, imagen: lamparas2, titulo: 'Proyecto 7' },
    { id: 8, imagen: lamparas2, titulo: 'Proyecto 8' },
  ];

  return (
    <>
      <Transition
        show={seccionAnimacion === 'Proyectos'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div
          id="Proyectos"
          className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
          <h2 className="text-4xl font-bold mb-8">Proyectos</h2>

          {/* Contenedor de proyectos */}
          <div className="overflow-y-auto flex flex-wrap justify-center gap-6 px-4 md:px-10">
            {proyectos.map((proyecto) => (
              <div
                key={proyecto.id}
                className="max-w-xs rounded-lg overflow-hidden shadow-lg"
              >
                <img
                  src={proyecto.imagen}
                  alt={proyecto.titulo}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4 bg-white">
                  <p className="text-lg font-bold">{proyecto.titulo}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </Transition>
    </>
  );
}

export default Proyectos;
