import React, { useState, useEffect } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import yo from '../image/yo.jpg';
import street from '../image/street.jpg';

function Slider() {
  const [loading, setLoading] = useState(true); // Estado para controlar la carga inicial
  const [showContent, setShowContent] = useState(false); // Estado para mostrar el contenido después de la animación

  useEffect(() => {
    // Simular una carga inicial durante 2 segundos
    const timeout = setTimeout(() => {
      setLoading(false); // Cambiar el estado para iniciar la animación de desaparición
    }, 2000);

    return () => clearTimeout(timeout);
  }, []);

  useEffect(() => {
    // Mostrar el contenido después de que termine la animación de desaparición
    if (!loading) {
      const timeout = setTimeout(() => {
        setShowContent(true);
      }, 1000); // Tiempo de espera después de que desaparezca la pantalla de carga

      return () => clearTimeout(timeout);
    }
  }, [loading]);

  return (
    <div className="relative">
      {/* Pantalla de carga inicial */}
      <Transition
        show={loading}
        enter="transition-opacity duration-1000"
        enterFrom="opacity-100"
        enterTo="opacity-0"
        leave="transition-opacity duration-1000"
        leaveFrom="opacity-0"
        leaveTo="opacity-100"
      >
        <div className="fixed inset-0 flex items-center justify-center bg-black text-white text-4xl font-extrabold">
          Carmelo Rodríguez
        </div>
      </Transition>

      {/* Contenido del portafolio */}
      {showContent && (
        <div
          id="Inicio"
          className="h-screen flex flex-col items-center justify-center"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${street})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
          <div className="grid grid-cols-2">
            <div className="col-span-1 flex flex-col justify-center w-full md:ml-80">
              <EfectoEscritura />

              <h2 className="text-3xl font-extrabold text-white mt-2">Frontend Developer</h2>
              <p className="text-white mt-3">
                Desde temprana edad, descubrí mi pasión por el arte y la creatividad. Esta inclinación artística me ha
                acompañado
              </p>
              <p className="text-white">
                a lo largo de los años y la he canalizado hacia el diseño web, fusionando estética y funcionalidad en
                cada proyecto.
              </p>
              <p className="text-white">
                Mi formación en Desarrollo de Aplicaciones Web, complementada con un fuerte enfoque en diseño, me
                permite crear
              </p>
              <p className="text-white">
                experiencias digitales que no solo son visualmente atractivas, sino también intuitivas y eficientes.
                Nacido en Huelva,
              </p>
              <p className="text-white">
                de Huelva, España, mi objetivo es aprovechar mi creatividad innata para desarrollar soluciones web
                innovadoras y
              </p>
              <p className="text-white">de alta calidad.</p>
            </div>
            <div className="col-span-1 flex items-center justify-center md:mr-52">
              <img src={yo} className="rounded-full w-72 h-72 object-cover hover:scale-110" alt="Mi Foto" />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Slider;
