import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import landingEscritorio from '../image/cabecera1.png';
import landingMovil from '../image/cabecera1.png';
import menuMovil from '../image/cabecera1.png';

function ProyectoDetalle4({ seccionAnimacion }) {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <Transition
      show={seccionAnimacion === 'ProyectoLandingPage'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="ProyectoLandingPage"
        className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-teal-100 text-white"
        style={{ background: 'rgba(0, 0, 0, 0.9)', minHeight: isMobile ? '3500px' : '4000px' }}
      >
        <div className="max-w-4xl px-6 py-12 mt-14 text-center">
          <h1 className="text-4xl font-bold mb-8 md:inline-block md:border-b-4">Landing Page - GameHaven</h1>
          <p className="text-lg text-gray-200 mb-8">
            La landing page de GameHaven es un proyecto de diseño web que presenta una interfaz moderna y adaptativa, optimizada tanto para dispositivos móviles como para escritorio. Utiliza una combinación de HTML, CSS y JavaScript para asegurar una experiencia de usuario fluida y accesible.
          </p>

          {/* Sección de Diseño en Escritorio */}
          <section className="mb-12">
            <h2 className="text-3xl font-semibold mb-4 md:inline-block md:border-b-4">Diseño en Escritorio</h2>
            <p className="text-lg text-gray-200 mb-4">
              La versión de escritorio de la landing page incluye un diseño limpio con navegación visible, imágenes de alta calidad, y una estructura optimizada para pantallas grandes.
            </p>
            <img src={landingEscritorio} alt="Diseño en Escritorio" className="mb-4 w-full" />
          </section>

          {/* Sección de Diseño en Móvil */}
          <section className="mb-12">
            <h2 className="text-3xl font-semibold mb-4 md:inline-block md:border-b-4">Diseño en Móvil</h2>
            <p className="text-lg text-gray-200 mb-4">
              La página ha sido cuidadosamente adaptada para dispositivos móviles, garantizando que todas las funciones y contenido sean fácilmente accesibles en pantallas más pequeñas.
            </p>
            <img src={landingMovil} alt="Diseño en Móvil" className="mb-4 w-full" />
          </section>

          {/* Sección del Menú Móvil */}
          <section className="mb-12">
            <h2 className="text-3xl font-semibold mb-4 md:inline-block md:border-b-4">Menú Móvil</h2>
            <p className="text-lg text-gray-200 mb-4">
              El menú de la landing page en dispositivos móviles es interactivo, accesible y fácil de navegar, permitiendo a los usuarios acceder rápidamente a las distintas secciones.
            </p>
            <img src={menuMovil} alt="Menú Móvil" className="mb-4 w-full" />
          </section>

          {/* Sección de Características Técnicas */}
          <section className="mb-12">
            <h2 className="text-3xl font-semibold mb-4 md:inline-block md:border-b-4">Características Técnicas</h2>
            <p className="text-lg text-gray-200 mb-4">
              La landing page está desarrollada utilizando HTML5, CSS3, y JavaScript. Su diseño responsivo está construido sobre un sistema de grid flexible y componentes reutilizables. Además, se ha optimizado para tiempos de carga rápidos y compatibilidad cross-browser.
            </p>
          </section>
        </div>
      </div>
    </Transition>
  );
}

export default ProyectoDetalle4;
