import React, { useEffect, useState } from 'react';

function TypingEffect({ text }) {
  const [typingText, setTypingText] = useState('');
  const fullText = text;

  useEffect(() => {
    const interval = setInterval(() => {
      const currentLength = typingText.length;
      const targetLength = fullText.length;

      if (currentLength < targetLength) {
        setTypingText(fullText.slice(0, currentLength + 1));
      } else {
        clearInterval(interval);
      }
    }, 100); // Ajusta la velocidad de escritura según sea necesario

    return () => clearInterval(interval);
  }, [typingText, fullText]);

  return (
    <h2 className="text-4xl font-normal text-white">
      ¡Hola, soy <span className='font-extrabold'>{typingText}</span>!
    </h2>
  );
}

export default TypingEffect;
