import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import InicioMovil from './InicioMovil'; // Importar el componente InicioMovil
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuMobileOpen, setMenuMobileOpen] = useState(false);

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearTimeout(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
    setMenuMobileOpen(false); // Cerrar el menú hamburguesa al hacer clic en un enlace
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto'
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >

        {/* Menú hamburguesa para móvil */}
        <div className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 px-6 z-50 md:hidden">
          <button
            onClick={() => setMenuMobileOpen(!menuMobileOpen)}
            className="text-gray-500 focus:outline-none"
            style={{ marginBottom: '1rem' }}
          >
            <svg
              className="w-6 h-6 text-ba"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              {menuMobileOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M4 6h16M4 12h16m-7 6h7"
                />
              )}
            </svg>
          </button>
          <div className={`absolute top-full left-0 right-0 bg-white shadow-md rounded-b-lg overflow-hidden transition-all duration-300 ease-in-out ${menuMobileOpen ? 'opacity-100 visible' : 'opacity-100 invisible'}`}>
            <button
              onClick={() => navegacion('Inicio')}
              className={`block py-3 px-6 w-full text-left ${
                seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-600'
              }`}
            >
              INICIO
            </button>
            <button
              onClick={() => navegacion('Tecnologias')}
              className={`block py-3 px-6 w-full text-left ${
                seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-600'
              }`}
            >
              TECNOLOGÍAS
            </button>
            <button
              onClick={() => navegacion('ExperienciayFormacion')}
              className={`block py-3 px-6 w-full text-left ${
                seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-600'
              }`}
            >
              EXPERIENCIA Y FORMACIÓN
            </button>
            <button
              onClick={() => navegacion('Proyectos')}
              className={`block py-3 px-6 w-full text-left ${
                seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-600'
              }`}
            >
              PROYECTOS
            </button>
          </div>
        </div>
        </Transition>

        {/* Contenido para escritorio (no mostrado en móvil) */}
        <nav className="hidden md:flex fixed top-0 left-0 right-0 justify-center items-center py-4 z-50">
          <div className="ml-96 mr-96 flex justify-center items-center rounded-full shadow-md bg-white">
            <button
              onClick={() => navegacion('Inicio')}
              className={`py-5 pr-10 pl-10 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${
                seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'
              }`}
            >
              INICIO
            </button>
            <button
              onClick={() => navegacion('Tecnologias')}
              className={`py-5 px-6 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'
              }`}
            >
              TECNOLOGÍAS
            </button>
            <button
              onClick={() => navegacion('ExperienciayFormacion')}
              className={`py-5 px-4 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'
              }`}
            >
              EXPERIENCIA Y FORMACIÓN
            </button>
            <button
              onClick={() => navegacion('Proyectos')}
              className={`py-5 pl-10 px-10 pr-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'
              }`}
            >
              PROYECTOS
            </button>
          </div>
        </nav>

        {/* Renderizar componente según el tamaño de la pantalla */}
        {!menuMobileOpen && (
          <>
            {window.innerWidth <= 768 ? (
              <InicioMovil seccionAnimacionMovil={seccionAnimacion} navegacion={navegacion} />
            ) : (
              <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
            )}
          </>
        )}

        <Tecnologias seccionAnimacion={seccionAnimacion} />

        <Experiencia seccionAnimacion={seccionAnimacion} />

        <Proyectos seccionAnimacion={seccionAnimacion} />

        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
