import React from 'react';
import { Transition } from '@headlessui/react';

function ProyectoDetalle1({ seccionAnimacion }) {
  return (
    <Transition
      show={seccionAnimacion === 'Proyecto1'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div id="Proyecto1" className="h-screen flex flex-col items-center justify-center bg-gray-900 text-white">
        <h1 className="text-4xl font-bold">Detalles del Proyecto 1</h1>
        <p className="mt-4 max-w-xl text-center">
          Aquí van los detalles del proyecto. Puedes incluir descripciones, imágenes, tecnologías usadas, etc.
        </p>
      </div>
    </Transition>
  );
}

export default ProyectoDetalle1;
