import React, { useState } from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import { Transition } from '@headlessui/react';

const proyectosData = [
  {
    id: 1,
    title: 'Proyecto 1',
    image: 'url de la imagen',
    technologies: ['React', 'Tailwind CSS', 'Firebase'],
  },
  {
    id: 2,
    title: 'Proyecto 2',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  {
    id: 2,
    title: 'Proyecto 3',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  {
    id: 2,
    title: 'Proyecto 2',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  {
    id: 2,
    title: 'Proyecto 2',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  // Add more projects as needed
];

const itemsPerPage = 3;

function Proyectos({ seccionAnimacion }) {
  const [currentPage, setCurrentPage] = useState(1);

  const lastIndex = currentPage * itemsPerPage;
  const firstIndex = lastIndex - itemsPerPage;
  const currentProjects = proyectosData.slice(firstIndex, lastIndex);

  const totalPages = Math.ceil(proyectosData.length / itemsPerPage);

  const nextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  const prevPage = () => {
    setCurrentPage((prev) => prev - 1);
  };

  return (
    <Transition
      show={seccionAnimacion === 'Proyectos'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="Proyectos"
        className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <h2 className="text-4xl font-bold mb-8">Proyectos</h2>
        <div className="overflow-y-auto max-h-96 w-4/5 bg-white rounded-lg shadow-lg p-4">
          {currentProjects.map((proyecto) => (
            <div
              key={proyecto.id}
              className="flex items-center justify-between bg-gray-100 p-4 mb-4 rounded-lg"
            >
              <div className="w-32 h-32 bg-gray-200 rounded-lg mr-4"></div>
              <div className="flex-1">
                <h3 className="font-bold text-lg">{proyecto.title}</h3>
                <div className="flex mt-2">
                  {proyecto.technologies.map((tech, index) => (
                    <span
                      key={index}
                      className="bg-blue-200 text-blue-800 rounded-full px-2 py-1 text-sm mr-2"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="mt-4 flex justify-center">
          <button
            onClick={prevPage}
            disabled={currentPage === 1}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-l"
          >
            Anterior
          </button>
          <button
            onClick={nextPage}
            disabled={currentPage === totalPages}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-r"
          >
            Siguiente
          </button>
        </div>
      </div>
    </Transition>
  );
}

export default Proyectos;
