import React from 'react'

function Nav() {

    const navegacion = (seccion) => {
        document.getElementById(seccion).scrollIntoView({behavior:'smooth'});
    }

  return (
    <>
       <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
  <div className="container mx-auto px-4">
    <nav className="flex justify-center space-x-4 py-4">
      <button onClick={() => navegacion('Inicio')} className="hover:underline">
        <p>Inicio</p>
      </button>
      <button onClick={() => navegacion('Tecnologías')} className="hover:underline">
        <p>Tecnologías</p>
      </button>
    </nav>
  </div>
</header>
    </>
  )
}

export default Nav
