import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

function NavMenu({ seccionAnimacion, navegacion }) {
  return (
    <div className="flex flex-col items-start bg-white shadow-lg py-4 px-6 space-y-2">
      <button
        onClick={() => navegacion('Inicio')}
        className={`py-2 px-4 focus:outline-none rounded-lg transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold bg-gray-200' : 'text-gray-700 hover:bg-gray-100'}`}
      >
        INICIO
      </button>
      <button
        onClick={() => navegacion('Tecnologias')}
        className={`py-2 px-4 focus:outline-none rounded-lg transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold bg-gray-200' : 'text-gray-700 hover:bg-gray-100'}`}
      >
        TECNOLOGÍAS
      </button>
      <button
        onClick={() => navegacion('ExperienciayFormacion')}
        className={`py-2 px-4 focus:outline-none rounded-lg transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold bg-gray-200' : 'text-gray-700 hover:bg-gray-100'}`}
      >
        EXPERIENCIA Y FORMACIÓN
      </button>
      <button
        onClick={() => navegacion('Proyectos')}
        className={`py-2 px-4 focus:outline-none rounded-lg transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold bg-gray-200' : 'text-gray-700 hover:bg-gray-100'}`}
      >
        PROYECTOS
      </button>
    </div>
  );
}

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuAbierto, setMenuAbierto] = useState(false);
  const [esDispositivoMovil, setEsDispositivoMovil] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setEsDispositivoMovil(window.innerWidth < 768); // Ajusta este valor según tus necesidades de diseño responsivo
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearTimeout(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto'
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  const toggleMenu = () => {
    setMenuAbierto(!menuAbierto);
  };

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <nav className="fixed top-0 left-0 bottom-0 flex flex-col justify-center items-start py-4 px-2 bg-gray-100 shadow-lg z-50">
            {!esDispositivoMovil && (
              <NavMenu seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
            )}

            {esDispositivoMovil && (
              <div className="md:hidden absolute top-0 left-0">
                <button
                  onClick={toggleMenu}
                  className="p-2 focus:outline-none"
                >
                  <svg
                    className="w-8 h-8 text-gray-700"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d={menuAbierto ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16m-7 6h7"}
                    ></path>
                  </svg>
                </button>
                {/* Menú desplegable para dispositivos móviles */}
                <Transition
                  show={menuAbierto}
                  enter="transition ease-out duration-100 transform"
                  enterFrom="opacity-0 scale-95"
                  enterTo="opacity-100 scale-100"
                  leave="transition ease-in duration-75 transform"
                  leaveFrom="opacity-100 scale-100"
                  leaveTo="opacity-0 scale-95"
                >
                  <div className="absolute top-0 left-0 bg-white shadow-lg py-2 z-50">
                    <NavMenu seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
                  </div>
                </Transition>
              </div>
            )}
          </nav>
        </Transition>

        <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
        <Tecnologias seccionAnimacion={seccionAnimacion} />
        <Experiencia seccionAnimacion={seccionAnimacion} />
        <Proyectos seccionAnimacion={seccionAnimacion} />
        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
