// Slider.js
import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';
import HamburguesaMenu from './HamburguesaMenu'; // Importa el nuevo componente

function Slider() {
  const [seccionAnimacion, setseccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearInterval(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setseccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto'
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <nav className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 px-4 z-50 bg-white shadow-md">
            <HamburguesaMenu seccionAnimacion={seccionAnimacion} navegacion={navegacion} /> {/* Agrega el menú hamburguesa */}
            <div className="hidden md:flex justify-center items-center space-x-4">
              <button
                onClick={() => navegacion('Inicio')}
                className={`py-5 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`py-5 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`py-5 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`py-5 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                PROYECTOS
              </button>
            </div>
          </nav>
        </Transition>

        <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
        <Tecnologias seccionAnimacion={seccionAnimacion} />
        <Experiencia seccionAnimacion={seccionAnimacion} />
        <Proyectos seccionAnimacion={seccionAnimacion} />
        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
