import React from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import { Transition } from '@headlessui/react';

const proyectosData = [
  {
    id: 1,
    title: 'Proyecto 1',
    image: 'url de la imagen',
    technologies: ['React', 'Tailwind CSS', 'Firebase'],
  },
  {
    id: 2,
    title: 'Proyecto 2',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  {
    id: 3,
    title: 'Proyecto 3',
    image: 'url de la imagen',
    technologies: ['HTML', 'CSS', 'JavaScript'],
  },
  {
    id: 4,
    title: 'Proyecto 4',
    image: 'url de la imagen',
    technologies: ['Vue.js', 'GraphQL', 'MongoDB'],
  },
  {
    id: 5,
    title: 'Proyecto 5',
    image: 'url de la imagen',
    technologies: ['React Native', 'Firebase', 'Expo'],
  },
  // Agrega más proyectos según sea necesario
];

function Proyectos({ seccionAnimacion }) {
  // Calcula la altura del contenedor para ajustar el scroll
  const calcularAlturaContenedor = () => {
    // Altura mínima para mostrar los primeros tres proyectos sin scroll
    const alturaMinima = 3 * (100 / 3); // 100% dividido entre 3 proyectos

    // Altura máxima para permitir el scroll a partir del cuarto proyecto
    const alturaMaxima = 96; // Altura fija para el contenedor

    return proyectosData.length > 3 ? `${alturaMaxima}px` : `${alturaMinima}px`;
  };

  return (
    <Transition
      show={seccionAnimacion === 'Proyectos'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="Proyectos"
        className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <h2 className="text-4xl font-bold mb-8">Proyectos</h2>
        <div
          className="overflow-y-auto bg-white rounded-lg shadow-lg p-4"
          style={{ maxHeight: calcularAlturaContenedor(), width: '80%' }}
        >
          {proyectosData.map((proyecto) => (
            <div
              key={proyecto.id}
              className="flex items-center justify-between bg-gray-100 p-4 mb-4 rounded-lg"
              style={{ minHeight: 'calc(100% / 3 - 1rem)' }} // Calcula la altura mínima de cada proyecto
            >
              <div className="w-32 h-32 bg-gray-200 rounded-lg mr-4"></div>
              <div className="flex-1">
                <h3 className="font-bold text-lg">{proyecto.title}</h3>
                <div className="flex mt-2">
                  {proyecto.technologies.map((tech, index) => (
                    <span
                      key={index}
                      className="bg-blue-200 text-blue-800 rounded-full px-2 py-1 text-sm mr-2"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Transition>
  );
}

export default Proyectos;
