import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

function Slider() {
  const [seccionAnimacion, setseccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuAbierto, setMenuAbierto] = useState(false); // Estado para controlar la visibilidad del menú

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearTimeout(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setseccionAnimacion(seccion);
    setMenuAbierto(false); // Cierra el menú cuando se selecciona una opción
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto'
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <nav className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 px-4 bg-white shadow-md z-50">
            <div className="ml-auto mr-auto flex items-center">
              <button
                onClick={() => setMenuAbierto(!menuAbierto)} // Alterna el estado del menú
                className="lg:hidden flex items-center justify-center p-2 focus:outline-none"
              >
                {/* Ícono de menú hamburguesa */}
                <svg
                  className="w-6 h-6"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              </button>
            </div>
            <div className="hidden lg:flex ml-auto mr-auto items-center rounded-full shadow-md bg-white">
              {/* Elementos de la navegación para escritorio */}
              <button
                onClick={() => navegacion('Inicio')}
                className={`py-5 pr-10 pl-10 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`py-5 px-6 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`py-5 px-4 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`py-5 pl-10 px-10 pr-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                PROYECTOS
              </button>
            </div>
          </nav>

          <Transition
            show={menuAbierto}
            enter="transition-opacity duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity duration-300"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-40" onClick={() => setMenuAbierto(false)}></div>
          </Transition>

          <Transition
            show={menuAbierto}
            enter="transition-transform duration-300"
            enterFrom="transform -translate-x-full"
            enterTo="transform translate-x-0"
            leave="transition-transform duration-300"
            leaveFrom="transform translate-x-0"
            leaveTo="transform -translate-x-full"
          >
            <div className="lg:hidden fixed inset-y-0 left-0 bg-white p-4 z-50 w-64 shadow-lg">
              {/* Menú para dispositivos móviles */}
              <button
                onClick={() => navegacion('Inicio')}
                className={`block w-full text-left py-2 px-4 mb-2 rounded transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold bg-gray-200' : 'text-gray-400 font-bold'}`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`block w-full text-left py-2 px-4 mb-2 rounded transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold bg-gray-200' : 'text-gray-400 font-bold'}`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`block w-full text-left py-2 px-4 mb-2 rounded transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold bg-gray-200' : 'text-gray-400 font-bold'}`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`block w-full text-left py-2 px-4 mb-2 rounded transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold bg-gray-200' : 'text-gray-400 font-bold'}`}
              >
                PROYECTOS
              </button>
            </div>
          </Transition>
        </Transition>

        <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
        <Tecnologias seccionAnimacion={seccionAnimacion} />
        <Experiencia seccionAnimacion={seccionAnimacion} />
        <Proyectos seccionAnimacion={seccionAnimacion} />
        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
