import React, { useEffect, useState } from 'react';

function EfectoEscritura() {
  const [escribir, setEscribir] = useState('');
  const nombre = "Carmelo Rodríguez | Portafolio";

  useEffect(() => {
    let mounted = true;
    let index = 0;

    const interval = setInterval(() => {
      if (mounted) {
        if (index <= nombre.length) {
          setEscribir(nombre.slice(0, index));
          index++;
        } else {
          clearInterval(interval);
        }
      }
    }, 100);

    return () => {
      mounted = false;
      clearInterval(interval);
    };
  }, []);

  return (
    <h2 className="text-5xl font-normal text-white">
      <span className="font-times font-extrabold">{escribir}</span>
    </h2>
  );
}

export default EfectoEscritura;
