import React, { useState } from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import { Transition } from '@headlessui/react';

const proyectosData = [
  {
    id: 1,
    title: 'Proyecto 1',
    image: 'url de la imagen',
    technologies: ['React', 'Tailwind CSS', 'Firebase'],
  },
  {
    id: 2,
    title: 'Proyecto 2',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
  {
    id: 3,
    title: 'Proyecto 3',
    image: 'url de la imagen',
    technologies: ['HTML', 'CSS', 'JavaScript'],
  },
  {
    id: 4,
    title: 'Proyecto 4',
    image: 'url de la imagen',
    technologies: ['React', 'Redux', 'Firebase'],
  },
  {
    id: 5,
    title: 'Proyecto 5',
    image: 'url de la imagen',
    technologies: ['Node.js', 'Express', 'MongoDB'],
  },
];

function Proyectos({ seccionAnimacion }) {
  const [currentPage, setCurrentPage] = useState(0);
  const projectsPerPage = 3; // Número de proyectos por página

  const nextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const prevPage = () => {
    setCurrentPage(currentPage - 1);
  };

  return (
    <Transition
      show={seccionAnimacion === 'Proyectos'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="Proyectos"
        className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white relative"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <h2 className="text-4xl font-bold mb-8">Proyectos</h2>
        <div className="overflow-hidden w-4/5 bg-white rounded-lg shadow-lg">
          <div className="flex transition-transform duration-300 ease-in-out transform -translate-x-full translate-x-0">
            {proyectosData
              .slice(currentPage * projectsPerPage, (currentPage + 1) * projectsPerPage)
              .map((proyecto) => (
                <div
                  key={proyecto.id}
                  className="w-full flex-shrink-0 flex justify-center items-center"
                >
                  <div className="flex flex-col items-center bg-gray-100 p-4 rounded-lg">
                    <div className="w-32 h-32 bg-gray-200 rounded-lg mb-4"></div>
                    <h3 className="font-bold text-lg mb-2">{proyecto.title}</h3>
                    <div className="flex flex-wrap justify-center">
                      {proyecto.technologies.map((tech, index) => (
                        <span
                          key={index}
                          className="bg-blue-200 text-blue-800 rounded-full px-2 py-1 text-sm mr-2 mb-2"
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
          </div>
          <button
            onClick={prevPage}
            className={`absolute top-1/2 transform -translate-y-1/2 left-4 bg-white text-gray-600 hover:text-gray-800 rounded-full p-2 transition-colors duration-300 ${currentPage === 0 ? 'invisible' : ''}`}
          >
            &#8592;
          </button>
          <button
            onClick={nextPage}
            className={`absolute top-1/2 transform -translate-y-1/2 right-4 bg-white text-gray-600 hover:text-gray-800 rounded-full p-2 transition-colors duration-300 ${
              (currentPage + 1) * projectsPerPage >= proyectosData.length ? 'invisible' : ''
            }`}
          >
            &#8594;
          </button>
        </div>
      </div>
    </Transition>
  );
}

export default Proyectos;
