import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

    function Slider() {

      const [seccionAnimacion, setseccionAnimacion] = useState('Inicio');
      const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
      const [menuAbierto, setMenuAbierto] = useState(false);

      const toggleMenu = () => {
        setMenuAbierto(!menuAbierto);
      };
      useEffect(() => {
        const tiempo = setTimeout(() => {
          setMostrarPantallaInicial(false);
        },2250);

        return () => clearInterval(tiempo);
      },[])

      const navegacion = (seccion) => {
        setseccionAnimacion(seccion);
        const elemento = document.getElementById(seccion);
        if(elemento){
          elemento.scrollIntoView({behavior:'smooth'})
        }
      }

      useEffect(()=>{
        const titulos = {
          Inicio : "Inicio",
          Tecnologias : "Tecnología",
          ExperienciayFormacion : "Experiencia y Formación",
          Proyectos : "Proyectos",
          Contacto : "Contacto"
        };
        document.title = titulos[seccionAnimacion];
      },[seccionAnimacion]);

      return (
        <>
          <div className="relative">
            <Transition
              show={mostrarPantallaInicial}
              enter="transition-opacity duration-1000"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity duration-1000"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
                <EfectoEscritura />
              </div>
            </Transition>

            <Transition
              show={!mostrarPantallaInicial}
              enter="transition-opacity duration-1000"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity duration-1000"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
            <nav className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 z-50 bg-white shadow-md px-4 md:px-8">
  <div className="flex justify-between items-center w-full md:w-auto">
    <button
      onClick={toggleMenu}
      className="text-gray-700 focus:outline-none md:hidden"
    >
      <svg
        className="w-8 h-8"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          d={menuAbierto ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16m-7 6h7"}
        ></path>
      </svg>
    </button>

    <div className="hidden md:flex items-center space-x-4">
      <button
        onClick={() => navegacion('Inicio')}
        className={`py-5 pr-10 pl-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
      >
        INICIO
      </button>
      <button
        onClick={() => navegacion('Tecnologias')}
        className={`py-5 px-6 pr-10 pl-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
      >
        TECNOLOGÍAS
      </button>
      <button
        onClick={() => navegacion('ExperienciayFormacion')}
        className={`py-5 px-4 pr-10 pl-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
      >
        EXPERIENCIA Y FORMACIÓN
      </button>
      <button
        onClick={() => navegacion('Proyectos')}
        className={`py-5 pl-10 px-10 pr-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
      >
        PROYECTOS
      </button>
    </div>
  </div>

  <div className={`md:hidden ${menuAbierto ? "block" : "hidden"}`}>
    <div className="flex flex-col items-center mt-4 space-y-4">
      <button
        onClick={() => { toggleMenu(); navegacion('Inicio'); }}
        className="text-gray-700"
      >
        INICIO
      </button>
      <button
        onClick={() => { toggleMenu(); navegacion('Tecnologias'); }}
        className="text-gray-700"
      >
        TECNOLOGÍAS
      </button>
      <button
        onClick={() => { toggleMenu(); navegacion('ExperienciayFormacion'); }}
        className="text-gray-700"
      >
        EXPERIENCIA Y FORMACIÓN
      </button>
      <button
        onClick={() => { toggleMenu(); navegacion('Proyectos'); }}
        className="text-gray-700"
      >
        PROYECTOS
      </button>
    </div>
  </div>
</nav>


            </Transition>
              
            <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion}/>

            <Tecnologias seccionAnimacion={seccionAnimacion}/>

            <Experiencia seccionAnimacion={seccionAnimacion}/>  

            <Proyectos seccionAnimacion={seccionAnimacion}/>

            <Contacto seccionAnimacion={seccionAnimacion}/>
          </div>
        </>
      );
    }

    export default Slider;
