import React, { useState, useEffect } from 'react';
import { Transition } from '@headlessui/react';

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Función para generar un número aleatorio dentro de un rango
  const randomInRange = (min, max) => {
    return Math.random() * (max - min) + min;
  };

  useEffect(() => {
    const stars = document.querySelectorAll('.star');
    stars.forEach(star => {
      star.style.setProperty('--top-offset', `${randomInRange(0, 100)}vh`);
      star.style.setProperty('--fall-duration', `${randomInRange(5, 15)}s`);
      star.style.setProperty('--fall-delay', `${randomInRange(0, 10)}s`);
      star.style.setProperty('--star-tail-length', `${randomInRange(5, 15)}em`);
    });
  }, []);

  return (
    <div className="relative">

      {/* Estilos Tailwind CSS para el cuerpo del documento */}
      <style jsx global>{`
        body {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background: radial-gradient(ellipse at bottom, #0d1d31 0%, #0c0d13 100%);
          overflow: hidden;
        }

        @function random_range($min, $max) {
          $rand: random();
          $random_range: $min + floor($rand * (($max - $min) + 1));
          @return $random_range;
        }

        .stars {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          transform: rotate(-45deg);
        }

        .star {
          --star-color: white;
          --star-tail-length: 6em;
          --star-tail-height: 2px;
          --star-width: calc(var(--star-tail-length) / 6);
          --fall-duration: 9s;
          --tail-fade-duration: var(--fall-duration);

          position: absolute;
          top: var(--top-offset);
          left: 0;
          width: var(--star-tail-length);
          height: var(--star-tail-height);
          color: var(--star-color);
          background: linear-gradient(45deg, currentColor, transparent);
          border-radius: 50%;
          filter: drop-shadow(0 0 6px currentColor);
          transform: translate3d(0, 0, 0);
          animation: fall var(--fall-duration) var(--fall-delay) linear infinite, tail-fade var(--tail-fade-duration) var(--fall-delay) ease-out infinite;

          @media screen and (max-width: 750px) {
            animation: fall var(--fall-duration) var(--fall-delay) linear infinite;
          }
        }

        @keyframes fall {
          to {
            transform: translate3d(-100vw, 100vh, 0);
          }
        }

        @keyframes tail-fade {
          0%,
          50% {
            width: var(--star-tail-length);
            opacity: 1;
          }

          70%,
          80% {
            width: 0;
            opacity: 0.4;
          }

          100% {
            width: 0;
            opacity: 0;
          }
        }

        @keyframes blink {
          50% {
            opacity: 0.3;
          }
        }
      `}</style>

      {/* Navigation */}
      <nav className="fixed top-0 left-0 right-0 flex justify-center items-center py-4 z-50 bg-white">
        <div className="ml-96 mr-96 flex justify-center items-center rounded-full shadow-md bg-white">
          <button
            onClick={() => navegacion('Inicio')}
            className={`py-2 px-4 focus:outline-none rounded-full ${
              seccionAnimacion === 'Inicio'
                ? 'bg-gray-200 text-gray-800'
                : 'hover:bg-gray-200 hover:text-gray-800'
            }`}
          >
            Inicio
          </button>
          <button
            onClick={() => navegacion('Tecnologias')}
            className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${
              seccionAnimacion === 'Tecnologias'
                ? 'bg-gray-200 text-gray-800'
                : 'hover:bg-gray-200 hover:text-gray-800'
            }`}
          >
            Tecnologías
          </button>
          <button
            onClick={() => navegacion('ExperienciayFormacion')}
            className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${
              seccionAnimacion === 'ExperienciayFormacion'
                ? 'bg-gray-200 text-gray-800'
                : 'hover:bg-gray-200 hover:text-gray-800'
            }`}
          >
            Experiencia y Formación
          </button>
          <button
            onClick={() => navegacion('Proyectos')}
            className={`py-2 px-4 focus:outline-none rounded-full ml-4 ${
              seccionAnimacion === 'Proyectos'
                ? 'bg-gray-200 text-gray-800'
                : 'hover:bg-gray-200 hover:text-gray-800'
            }`}
          >
            Proyectos
          </button>
        </div>
      </nav>

      {/* Sección "Inicio" con animación de estrellas */}
      <Transition
        show={seccionAnimacion === 'Inicio'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-x-full"
        enterTo="opacity-100 transform translate-x-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-x-0"
        leaveTo="opacity-0 transform -translate-x-full"
      >
        <div id="Inicio" className="h-screen flex flex-col items-center justify-center relative">
          {/* Estrellas */}
          <div className="stars">
            {Array.from({ length: 50 }).map((_, index) => (
              <div
                key={index}
                className="star"
              ></div>
            ))}
          </div>

          {/* Contenido visible en la sección de Inicio */}
          <div className="relative z-10 text-center text-white">
            <h2 className="text-4xl font-bold">Inicio</h2>
            <p className="mt-2">Scroll Down for next slide</p>
          </div>
        </div>
      </Transition>

      <Transition
        show={seccionAnimacion === 'Tecnologias'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-x-full"
        enterTo="opacity-100 transform translate-x-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-x-0"
        leaveTo="opacity-0 transform translate-x-full"
      >
        <div id="Tecnologias" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white">
          <h2 className="text-4xl font-bold">Tecnologías</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
      </Transition>

      <Transition
        show={seccionAnimacion === 'ExperienciayFormacion'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform -translate-y-full"
      >
        <div id="ExperienciayFormacion" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white">
          <h2 className="text-4xl font-bold">Experiencia y Formación</h2>
          <p className="mt-2">Scroll Down</p>
        </div>
      </Transition>

      <Transition
        show={seccionAnimacion === 'Proyectos'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div id="Proyectos" className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black">
          <h2 className="text-4xl font-bold">Proyectos</h2>
        </div>
      </Transition>

    </div>
  );
}

export default Slider;
