// HamburguesaMenu.js
import React, { useState } from 'react';

function HamburguesaMenu({ seccionAnimacion, navegacion }) {
  const [abierto, setAbierto] = useState(false);

  const toggleMenu = () => {
    setAbierto(!abierto);
  };

  return (
    <div className="relative">
      <button
        onClick={toggleMenu}
        className="text-2xl p-2 rounded focus:outline-none"
      >
        &#9776; {/* Símbolo de hamburguesa */}
      </button>
      {abierto && (
        <div className="absolute top-0 left-0 w-full h-screen bg-white flex flex-col items-center justify-center">
          <button
            onClick={() => {
              navegacion('Inicio');
              toggleMenu();
            }}
            className={`py-4 text-xl ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
          >
            INICIO
          </button>
          <button
            onClick={() => {
              navegacion('Tecnologias');
              toggleMenu();
            }}
            className={`py-4 text-xl ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
          >
            TECNOLOGÍAS
          </button>
          <button
            onClick={() => {
              navegacion('ExperienciayFormacion');
              toggleMenu();
            }}
            className={`py-4 text-xl ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
          >
            EXPERIENCIA Y FORMACIÓN
          </button>
          <button
            onClick={() => {
              navegacion('Proyectos');
              toggleMenu();
            }}
            className={`py-4 text-xl ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
          >
            PROYECTOS
          </button>
        </div>
      )}
    </div>
  );
}

export default HamburguesaMenu;
