import React, { useState } from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import reactIcon from '../image/react.png';
import nodejsIcon from '../image/nodejs.png';
import { Transition } from '@headlessui/react';

const proyectos = [
  {
    id: 1,
    imagen: lamparas2,
    nombre: 'Tienda de ropa / EnsembleElegance',
    tecnologias: ['React', 'Laravel', 'Docker', 'Tailwind CSS'],
    descripcion:
      'Descripción del proyecto 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    enlace: 'https://ejemplo.com/proyecto1',
  },
  {
    id: 2,
    imagen: lamparas2,
    nombre: 'Portafolio',
    tecnologias: ['React', 'Tailwind CSS', 'MongoDB'],
    descripcion:
      'Descripción del proyecto 2. Ut enim ad minim veniam, quis nostrud exercitation ullamco.',
    enlace: 'https://ejemplo.com/proyecto2',
  },
];

const Proyectos = ({ seccionAnimacion, navegacion }) => {
  const [currentPage, setCurrentPage] = useState(1);

  const projectsPerPage = 3;
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = proyectos.slice(indexOfFirstProject, indexOfLastProject);

  let gridColumns = 'grid-cols-1';
  if (currentProjects.length >= 3) {
    gridColumns = 'md:grid-cols-3';
  } else if (currentProjects.length === 2) {
    gridColumns = 'md:grid-cols-2';
  }

  const getIcon = (tecnologia) => {
    switch (tecnologia.toLowerCase()) {
      case 'react':
        return reactIcon;
      case 'node.js':
        return nodejsIcon;
      default:
        return null;
    }
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <Transition
      show={seccionAnimacion === 'Proyectos'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="Proyectos"
        className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          minHeight: isMobile ? '700vh' : '805vh'
        }}
      >
        <div className={`grid gap-8 ${gridColumns}`}>
          {currentProjects.map((proyecto, index) => (
            <div key={proyecto.id} className="bg-white text-black p-4 rounded-lg">
              <img
                src={proyecto.imagen}
                alt={proyecto.nombre}
                className="w-full h-60 object-cover rounded-md mb-4"
              />
              <h3 className="text-lg font-semibold text-center">{proyecto.nombre}</h3>
              <div className="flex flex-wrap gap-2 mt-3 justify-center">
                {proyecto.tecnologias.map((tecnologia, index) => (
                  <span
                    key={index}
                    className="flex items-center px-2 py-1 bg-black text-white rounded-md text-sm"
                    style={{ fontSize: '0.9rem' }}
                  >
                    {getIcon(tecnologia) && (
                      <img src={getIcon(tecnologia)} alt={tecnologia} className="w-6 h-6 mr-2" />
                    )}
                    {tecnologia}
                  </span>
                ))}
              </div>
              <p className="text-sm mt-3 text-center">{proyecto.descripcion}</p>
              {index === 0 && (
                <button
                  onClick={() => navegacion('Proyecto1')}
                  className="mt-3 inline-block bg-black text-white px-4 py-2 rounded-md hover:bg-gray-800"
                >
                  Ver más detalles
                </button>
              )}
            </div>
          ))}
        </div>

        {/* Paginación */}
        <div className="mt-8">
          <ul className="flex space-x-2">
            {Array.from({ length: Math.ceil(proyectos.length / projectsPerPage) }).map(
              (_, index) => (
                <li
                  key={index}
                  onClick={() => paginate(index + 1)}
                  className={`cursor-pointer px-3 py-1 rounded-md ${
                    currentPage === index + 1
                      ? 'bg-black text-white font-bold border-2 border-white'
                      : 'bg-white text-black'
                  }`}
                >
                  {index + 1}
                </li>
              )
            )}
          </ul>
        </div>
      </div>
    </Transition>
  );
};

export default Proyectos;
