import React, { useState } from 'react';
import lamparas2 from '../image/lamparas2.jpg';
import DetalleProyecto from './DetalleProyecto';

const proyectos = [
  {
    id: 1,
    imagen: lamparas2,
    nombre: 'Tienda de ropa / EnsembleElegance',
    tecnologias: ['React', 'Laravel', 'Docker', 'Tailwind CSS'],
    descripcion:
      'Descripción del proyecto 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    enlace: 'https://www.ejemplo.com/proyecto1',
  },
  {
    id: 2,
    imagen: lamparas2,
    nombre: 'Portafolio',
    tecnologias: ['React', 'Tailwind CSS', 'MongoDB'],
    descripcion:
      'Descripción del proyecto 2. Ut enim ad minim veniam, quis nostrud exercitation ullamco.',
    enlace: 'https://www.ejemplo.com/proyecto2',
  },
];

const Proyectos = ({ seccionAnimacion }) => {
  const [mostrarDetalle, setMostrarDetalle] = useState(false);
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState(null);

  const mostrarDetalleProyecto = (proyecto) => {
    setProyectoSeleccionado(proyecto);
    setMostrarDetalle(true);
  };

  const cerrarDetalleProyecto = () => {
    setProyectoSeleccionado(null);
    setMostrarDetalle(false);
  };

  return (
    <div
      id="Proyectos"
      className={`h-screen flex flex-col items-center justify-center bg-gradient-to-b from-white to-black ${
        seccionAnimacion === 'Proyectos' ? 'opacity-100 transform translate-y-0' : 'opacity-0 transform translate-y-full'
      }`}
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      {/* Renderizar el detalle del proyecto si mostrarDetalle es true */}
      {mostrarDetalle && (
        <DetalleProyecto proyecto={proyectoSeleccionado} onClose={cerrarDetalleProyecto} />
      )}

      <h2 className="text-4xl font-bold">Proyectos</h2>
      <div className="mt-8 grid grid-cols-1 gap-8 md:grid-cols-3">
        {proyectos.map((proyecto) => (
          <div key={proyecto.id} className="bg-white p-4 rounded-lg shadow-md">
            <img src={proyecto.imagen} alt={proyecto.nombre} className="w-full h-60 object-cover rounded-md mb-4" />
            <h3 className="text-lg font-semibold">{proyecto.nombre}</h3>
            <div className="flex flex-wrap gap-2 mt-2">
              {proyecto.tecnologias.map((tecnologia, index) => (
                <span
                  key={index}
                  className="flex items-center px-2 py-1 bg-gray-200 text-gray-800 rounded-md text-sm"
                  style={{ fontSize: '0.9rem' }}
                >
                  {getIcon(tecnologia) && (
                    <img src={getIcon(tecnologia)} alt={tecnologia} className="w-6 h-6 mr-2" />
                  )}
                  {tecnologia}
                </span>
              ))}
            </div>
            <p className="text-sm mt-2">{proyecto.descripcion}</p>
            <button
              onClick={() => mostrarDetalleProyecto(proyecto)}
              className="mt-4 inline-block bg-black text-white px-4 py-2 rounded-md hover:bg-gray-800"
            >
              Ver más detalles
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Proyectos;
