import React from 'react'

function Nav() {

    const navegacion = (seccion) => {
        document.getElementById(seccion).scrollIntoView({ behavior : 'smooth'});
    };

    return (
        <>
          <header className="fixed top-0 left-0 right-0 bg-white text-gray-800 z-50 flex justify-center items-center h-16">
            <div className="flex justify-center items-center w-full">
              <nav className="flex justify-center items-center rounded-full shadow-md">
                <button onClick={() => navegacion('Inicio')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full">
                  Inicio
                </button>
                <button onClick={() => navegacion('Tecnologias')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full">
                  Tecnologías
                </button>
                <button onClick={() => navegacion('ExperienciayFormacion')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full">
                  Experiencia y Formación
                </button>
                <button onClick={() => navegacion('Proyectos')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full">
                  Proyectos
                </button>
              </nav>
            </div>
          </header>
        </>
      );
      
}

export default Nav
