import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';
import { MenuIcon, XIcon } from '@heroicons/react/solid'; // Para los iconos de hamburguesa y cierre

function Slider() {
  const [seccionAnimacion, setseccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuAbierto, setMenuAbierto] = useState(false); // Estado para el menú móvil

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearTimeout(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setseccionAnimacion(seccion);
    setMenuAbierto(false); // Cerrar el menú móvil al hacer clic en una sección
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const titulos = {
      Inicio: "Inicio",
      Tecnologias: "Tecnología",
      ExperienciayFormacion: "Experiencia y Formación",
      Proyectos: "Proyectos",
      Contacto: "Contacto",
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <nav className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 px-4 z-50 bg-white shadow-md">
            {/* Botón del menú tipo hamburguesa para móviles */}
            <button
              onClick={() => setMenuAbierto(!menuAbierto)}
              className="lg:hidden focus:outline-none text-black"
            >
              {menuAbierto ? (
                <XIcon className="h-6 w-6" />
              ) : (
                <MenuIcon className="h-6 w-6" />
              )}
            </button>

            {/* Menú completo para pantallas grandes */}
            <div className="hidden lg:flex justify-center items-center ml-auto">
              <button
                onClick={() => navegacion('Inicio')}
                className={`py-5 pr-10 pl-10 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`py-5 px-6 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`py-5 px-4 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`py-5 pl-10 px-10 pr-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                PROYECTOS
              </button>
            </div>
          </nav>

          {/* Menú desplegable para móviles */}
          <Transition
            show={menuAbierto}
            enter="transition-opacity duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity duration-300"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="lg:hidden fixed inset-0 bg-white z-40 flex flex-col items-center justify-center space-y-6">
              <button
                onClick={() => navegacion('Inicio')}
                className={`py-5 px-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`py-5 px-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`py-5 px-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`py-5 px-10 focus:outline-none rounded-full transition duration-300 ease-in-out ${seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'}`}
              >
                PROYECTOS
              </button>
            </div>
          </Transition>
        </Transition>

        <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
        <Tecnologias seccionAnimacion={seccionAnimacion} />
        <Experiencia seccionAnimacion={seccionAnimacion} />
        <Proyectos seccionAnimacion={seccionAnimacion} />
        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
