import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import lamparas2 from '../image/lamparas2.jpg';

function ProyectoDetalle2({ seccionAnimacion }) {

  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <>
      <Transition
        show={seccionAnimacion === 'Proyecto2'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div
          id="Proyecto2"
          className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            minHeight: isMobile ? '150vh' : '100vh'
          }}
        >
          <div className="w-full max-w-4xl p-8 bg-white text-black rounded-md shadow-lg">
            <h1 className="text-3xl font-semibold mb-4 text-center">Portafolio Personal</h1>
            <p className="text-sm mb-6 text-center">Una plataforma para mostrar mis habilidades, proyectos y formaci칩n. He utilizado <strong>React</strong> para una interfaz din치mica y <strong>Tailwind CSS</strong> para un dise침o elegante.</p>
            
            <div className="flex flex-col md:flex-row justify-center items-center mb-4">
              <img src={reactIcon} alt="React" className="w-12 h-12 mb-4 md:mb-0 md:mr-4" />
              <img src={tailwindIcon} alt="Tailwind CSS" className="w-12 h-12" />
            </div>

            <div className="text-center mt-8">
              <button
                onClick={() => window.location.href = 'https://github.com/tu-repositorio'}
                className="bg-black text-white px-4 py-2 rounded-md"
              >
                Ver Proyecto en GitHub
              </button>
            </div>
          </div>
        </div>
      </Transition>
    </>
  );
}

export default ProyectoDetalle2;
