import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import luna from '../image/luna.jpg';
import street from '../image/street.jpg';
import yo from '../image/yo.jpg';
import EfectoEscritura from './EfectoEscritura';

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');
  const [mostrarIntro, setMostrarIntro] = useState(true); // Estado para controlar la introducción

  useEffect(() => {
    // Después de 3 segundos, ocultar la introducción
    const timeout = setTimeout(() => {
      setMostrarIntro(false);
    }, 3000);

    return () => clearTimeout(timeout);
  }, []);

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="relative">
      {/* Introducción inicial */}
      {mostrarIntro && (
        <div
          className="fixed inset-0 flex items-center justify-center bg-black text-white"
          style={{ opacity: mostrarIntro ? 1 : 0, transition: 'opacity 1s ease' }}
        >
          <h1 className="text-4xl font-bold">Carmelo Rodríguez</h1>
        </div>
      )}

      {/* Contenido del portafolio con transiciones */}
      <Transition
        show={!mostrarIntro && seccionAnimacion === 'Inicio'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-x-full"
        enterTo="opacity-100 transform translate-x-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-x-0"
        leaveTo="opacity-0 transform -translate-x-full"
      >
        <div
          id="Inicio"
          className="h-screen flex flex-col items-center justify-center"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${street})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
          <div className="grid grid-cols-2">
            <div className="col-span-1 flex flex-col justify-center w-full md:ml-80">
              <EfectoEscritura />

              <h2 className="text-3xl font-extrabold text-white mt-2">Frontend Developer</h2>
              <p className="text-white mt-3">
                Desde temprana edad, descubrí mi pasión por el arte y la creatividad. Esta inclinación artística me ha
                acompañado
              </p>
              <p className="text-white">
                a lo largo de los años y la he canalizado hacia el diseño web, fusionando estética y funcionalidad en
                cada proyecto.
              </p>
              <p className="text-white">
                Mi formación en Desarrollo de Aplicaciones Web, complementada con un fuerte enfoque en diseño, me
                permite crear
              </p>
              <p className="text-white">
                experiencias digitales que no solo son visualmente atractivas, sino también intuitivas y eficientes.
                Nacido en Huelva,
              </p>
              <p className="text-white">
                de Huelva, España, mi objetivo es aprovechar mi creatividad innata para desarrollar soluciones web
                innovadoras y
              </p>
              <p className="text-white">de alta calidad.</p>
            </div>
            <div className="col-span-1 flex items-center justify-center md:mr-52">
              <img src={yo} className="rounded-full w-72 h-72 object-cover hover:scale-110" alt="Carmelo Rodríguez" />
            </div>
          </div>
        </div>
      </Transition>

      <Transition
        show={!mostrarIntro && seccionAnimacion === 'Tecnologias'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-x-full"
        enterTo="opacity-100 transform translate-x-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-x-0"
        leaveTo="opacity-0 transform translate-x-full"
      >
        <div
          id="Tecnologias"
          className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white"
        >
          <h2 className="text-4xl font-bold">Tecnologías</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
      </Transition>

      <Transition
        show={!mostrarIntro && seccionAnimacion === 'ExperienciayFormacion'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform -translate-y-full"
      >
        <div
          id="ExperienciayFormacion"
          className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white"
        >
          <h2 className="text-4xl font-bold">Experiencia y Formación</h2>
          <p className="mt-2">Scroll Down</p>
        </div>
      </Transition>

      <Transition
        show={!mostrarIntro && seccionAnimacion === 'Proyectos'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div
          id="Proyectos"
          className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black"
        >
          <h2 className="text-4xl font-bold">Proyectos</h2>
        </div>
      </Transition>
    </div>
  );
}

export default Slider;
