import { Transition } from '@headlessui/react';
import React from 'react';
import lamparas2 from '../image/lamparas2.jpg'; // Ejemplo de imagen de fondo

const proyectos = [
  {
    id: 1,
    title: 'Proyecto 1',
    description: 'Descripción del Proyecto 1',
    image: 'https://via.placeholder.com/300', // URL de la imagen del proyecto
  },
  {
    id: 2,
    title: 'Proyecto 2',
    description: 'Descripción del Proyecto 2',
    image: 'https://via.placeholder.com/300', // URL de la imagen del proyecto
  },
  {
    id: 3,
    title: 'Proyecto 3',
    description: 'Descripción del Proyecto 3',
    image: 'https://via.placeholder.com/300', // URL de la imagen del proyecto
  },
  {
    id: 4,
    title: 'Proyecto 4',
    description: 'Descripción del Proyecto 4',
    image: 'https://via.placeholder.com/300', // URL de la imagen del proyecto
  },
  {
    id: 5,
    title: 'Proyecto 5',
    description: 'Descripción del Proyecto 5',
    image: 'https://via.placeholder.com/300', // URL de la imagen del proyecto
  },
];

function Proyectos({ seccionAnimacion }) {
  return (
    <>
      <Transition
        show={seccionAnimacion === 'Proyectos'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div
          id="Proyectos"
          className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white overflow-y-auto"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${lamparas2})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
          <h2 className="text-4xl font-bold mb-8">Proyectos</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl px-4">
            {proyectos.map((proyecto) => (
              <div
                key={proyecto.id}
                className="bg-white shadow-lg rounded-lg overflow-hidden w-full"
              >
                <img
                  src={proyecto.image}
                  alt={proyecto.title}
                  className="w-full h-56 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-bold mb-2">{proyecto.title}</h3>
                  <p className="text-gray-700">{proyecto.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </Transition>
    </>
  );
}

export default Proyectos;
