import React from 'react';

const Starfall = () => {
  const starCount = 50; // Cantidad de estrellas
  const stars = Array.from({ length: starCount });

  const randomRange = (min, max) => {
    return min + Math.floor(Math.random() * (max - min + 1));
  };

  return (
    <div className="stars fixed top-0 left-0 w-full h-full overflow-hidden z-0">
      {stars.map((_, index) => (
        <div
          key={index}
          className="star absolute bg-white bg-opacity-60 rounded-full animate-star"
          style={{
            top: `${randomRange(-100, 0)}vh`,
            left: `${randomRange(0, 100)}vw`,
            width: `${randomRange(2, 4)}px`,
            height: `${randomRange(2, 4)}px`,
            animationDuration: `${randomRange(6, 12)}s`,
            animationDelay: `${randomRange(0, 6)}s`,
          }}
        ></div>
      ))}
    </div>
  );
};

export default Starfall;
