import React, { useState } from 'react';
import DetalleProyecto from './DetalleProyecto'; // Asegúrate de importar correctamente el componente DetalleProyecto

const Proyectos = () => {
  const [proyectoDetalle, setProyectoDetalle] = useState(null); // Estado para controlar qué proyecto se muestra en detalle

  // Función para abrir el detalle del proyecto
  const mostrarDetalle = (proyecto) => {
    setProyectoDetalle(proyecto);
  };

  // Función para cerrar el detalle del proyecto
  const cerrarDetalle = () => {
    setProyectoDetalle(null);
  };

  return (
    <div>
      {/* Renderizar lista de proyectos */}
      {proyectos.map((proyecto) => (
        <div key={proyecto.id}>
          {/* Renderizar cada proyecto */}
          <button onClick={() => mostrarDetalle(proyecto)}>Ver detalles</button>
        </div>
      ))}

      {/* Renderizar detalle del proyecto si proyectoDetalle no es null */}
      {proyectoDetalle && (
        <DetalleProyecto proyecto={proyectoDetalle} onClose={cerrarDetalle} />
      )}
    </div>
  );
};

export default Proyectos;
