import React from 'react';

function Nav() {
    const navegacion = (seccion, behavior = 'smooth', block = 'start', inline = 'nearest') => {
        document.getElementById(seccion).scrollIntoView({ behavior, block, inline },100);
    };

    return (
        <>
            <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
                <nav className="mx-auto p-4 flex justify-center space-x-4">
                    <button onClick={() => navegacion('Inicio')}>
                        <p>INICIO</p>
                    </button>

                    <button onClick={() => navegacion('Tecnologias', 'smooth', 'start', 'center')}>
                        <p>TECNOLOGÍAS</p>
                    </button>

                    <button onClick={() => navegacion('experienciayeducacion', 'smooth', 'start', 'end')}>
                        <p>EXPERIENCIA Y EDUCACIÓN</p>
                    </button>

                    <button onClick={() => navegacion('Contacto', 'smooth', 'start', 'end')}>
                        <p>CONTACTO</p>
                    </button>
                </nav>
            </header>
        </>
    );
}

export default Nav;
