import React from 'react';

function Nav() {
    const handleNavigation = (seccion, behavior = 'smooth', block = 'start', inline = 'nearest') => {
        document.getElementById(seccion).scrollIntoView({ behavior, block, inline });
    };

    return (
        <header className="fixed top-0 left-0 right-0 z-50 bg-gray-900 text-white">
            <nav className="container mx-auto p-4 flex justify-center space-x-4">
                <button onClick={() => handleNavigation('Inicio')} className="hover:underline">
                    Inicio
                </button>
                <button onClick={() => handleNavigation('Tecnologias', 'smooth', 'start', 'center')} className="hover:underline">
                    Tecnologías
                </button>
                <button onClick={() => handleNavigation('experienciayeducacion', 'smooth', 'start', 'end')} className="hover:underline">
                    Experiencia y Educación
                </button>
                <button onClick={() => handleNavigation('Contacto', 'smooth', 'start', 'end')} className="hover:underline">
                    Contacto
                </button>
            </nav>
        </header>
    );
}

export default Nav;
