import React, { useEffect, useState } from 'react';

function EfectoEscritura() {
  const [typingText, setTypingText] = useState('');
  const fullName = 'Carmelo Rodríguez';

  useEffect(() => {
    const interval = setInterval(() => {
      const currentLength = typingText.length;
      const targetLength = fullName.length;

      if (currentLength < targetLength) {
        setTypingText(fullName.slice(0, currentLength + 1));
      } else {
        clearInterval(interval);
      }
    }, 90); 

    return () => clearInterval(interval);
  }, [typingText, fullName]);

  return (
    <h2 className="text-4xl font-normal text-white">
      ¡Hola, soy <span className='font-extrabold'>{typingText}</span>!
    </h2>
  );
}

export default EfectoEscritura;
