import React, { useRef } from 'react';

function Slider() {
  const scrollPositions = useRef({});
  
  const navegacion = (seccion) => {
    const targetElement = document.getElementById(seccion);
    if (!targetElement) return;

    // Almacenar la posición actual de desplazamiento antes de desplazarse
    scrollPositions.current[seccion] = window.scrollY;

    targetElement.scrollIntoView({ behavior: 'smooth' });
  };

  const volver = (seccion) => {
    const position = scrollPositions.current[seccion];
    if (position !== undefined) {
      window.scrollTo({ top: position, behavior: 'smooth' });
    }
  };

  return (
    <div className="relative">
      <nav className="fixed top-0 left-0 right-0 flex justify-center items-center py-4 z-50">
        <div className="ml-96 mr-96 flex justify-center items-center rounded-full shadow-md bg-white">
          <button onClick={() => navegacion('Inicio')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full">
            Inicio
          </button>
          <button onClick={() => navegacion('Tecnologias')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4">
            Tecnologías
          </button>
          <button onClick={() => navegacion('ExperienciayFormacion')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4">
            Experiencia y Formación
          </button>
          <button onClick={() => navegacion('Proyectos')} className="hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4">
            Proyectos
          </button>
        </div>
      </nav>

      <div className="relative">
        <div id="Inicio" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-green-200 to-blue-200">
          <h2 className="text-4xl font-bold">The First slide</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
        <div id="Tecnologias" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white">
          <h2 className="text-4xl font-bold">The Second slide</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
        <div id="ExperienciayFormacion" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white">
          <h2 className="text-4xl font-bold">The Third slide</h2>
          <p className="mt-2">Scroll Down</p>
        </div>
        <div id="Proyectos" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black">
          <h2 className="text-4xl font-bold">The Fourth slide</h2>
        </div>
      </div>
    </div>
  );
}

export default Slider;
