import Nav from "./components/Nav";
import Slider from "./components/Slider";

function App() {

  const navegacion = (seccion, behavior = 'smooth', block = 'start', inline = 'nearest') => {
    document.getElementById(seccion).scrollIntoView({behavior, block, inline});
}

  return (
    <>
    <div>
    <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
            <nav className="mx-auto p-4 flex justify-center space-x-4">
                <button onClick={()=>navegacion('Inicio')} className="hover:underline">
                    <p>INICIO</p>
                </button>  

                <button onClick={()=>navegacion('Tecnologias', 'smooth', 'center')} className="hover:underline">
                    <p>TECNOLOGÍAS</p>
                </button>

                <button onClick={()=>navegacion('experienciayeducacion', 'smooth', 'end')} className="hover:underline">
                    <p>EXPERIENCIA Y EDUCACIÓN</p>
                </button>

                <button onClick={()=>navegacion('Contacto', 'smooth', 'start', 'end')} className="hover:underline">
                    <p>CONTACTO</p>
                </button>
            </nav>
        </header>
    <Slider/>

    </div>

    </>
  );
}

export default App;
