import React, { useEffect, useState } from 'react';

function EfectoEscritura() {
  const [escribir, setEscribir] = useState('');
  const nombre = "Carmelo Rodríguez | Portafolio";

  useEffect(() => {
    const intervalo = setInterval(() => {
      const longitud = escribir.length;
      const focusLongitud = nombre.length;

      if (longitud < focusLongitud) {
        setEscribir(nombre.slice(0, longitud + 1));
      } else {
        clearInterval(intervalo);
      }
    }, 45);

    return () => clearInterval(intervalo);
  }, [escribir, nombre]);

  return (
    <h2 className="text-5xl font-normal text-white">
      <span className="font-times font-extrabold">
        {escribir.split('').map((letra, index) => (
          <span
            key={index}
            className="transicion-letras"
            style={{ transitionDelay: `${index * 0.1}s` }}
          >
            {letra}
          </span>
        ))}
      </span>
    </h2>
  );
}

export default EfectoEscritura;
