import React from 'react';

function Slider() {
    return (
        <div className="relative mt-16">
            <div id="inicio" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-green-200 to-blue-200">
                <h2 className="text-4xl font-bold">Inicio</h2>
                <p className="mt-2">Scroll Down for next slide</p>
            </div>
            <div id="tecnologias" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white">
                <h2 className="text-4xl font-bold">Tecnologías</h2>
                <p className="mt-2">Scroll Down for next slide</p>
            </div>
            <div id="experienciayeducacion" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white">
                <h2 className="text-4xl font-bold">Experiencia y Educación</h2>
                <p className="mt-2">Scroll Down</p>
            </div>
            <div id="contacto" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black">
                <h2 className="text-4xl font-bold">Contacto</h2>
            </div>
        </div>
    );
}

export default Slider;
