import React from 'react';

function Nav() {
  const navegacion = (seccion) => {
    document.getElementById(seccion).scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
      <nav className="container mx-auto p-4 flex justify-center space-x-4">
        <button onClick={() => navegacion('Inicio')} className="hover:underline">
          INICIO
        </button>

        <button onClick={() => navegacion('Tecnologias')} className="hover:underline">
          TECNOLOGÍAS
        </button>

        <button onClick={() => navegacion('experienciayeducacion')} className="hover:underline">
          EXPERIENCIA Y EDUCACIÓN
        </button>
      </nav>
    </header>
  );
}

export default Nav;
