import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import portafolioHeader from '../image/portafolioHeader.jpg';


function ProyectoDetalle2({ seccionAnimacion }) {

  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const descargarDocumentacion = () => {
    const link = document.createElement('a');
    link.href = process.env.PUBLIC_URL + '/DocumentacionPortafolio.pdf';
    link.download = 'DocumentacionPortafolio.pdf';
    link.click();
  };

  const descargarGuiaEstilo = () => {
    const link = document.createElement('a');
    link.href = process.env.PUBLIC_URL + '/GuiaEstiloPortafolio.pdf';
    link.download = 'GuiaEstiloPortafolio.pdf';
    link.click();
  };

  return (
    <>
      <Transition
        show={seccionAnimacion === 'Proyecto2'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform translate-y-full"
      >
        <div
          id="Proyecto2"
          className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${portafolioHeader})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            minHeight: isMobile ? '700vh' : '805vh'
          }}
        >
          <div className="max-w-4xl px-6 py-12 mt-14">
            <h2 className="text-3xl font-semibold mb-4 text-center md:inline-block md:border-b-4">
              Proyecto de Portafolio Personal
            </h2>
            <p className="text-lg text-gray-200">
              Este proyecto es mi portafolio personal, creado para mostrar mis habilidades, proyectos y formación. Utilicé <strong>React</strong> para la funcionalidad de la interfaz y <strong>Tailwind CSS</strong> para un diseño estilizado y adaptable.
            </p>
            <h3 className="text-xl font-semibold mt-8 mb-2">Aspectos Clave del Proyecto:</h3>
            <ul className="list-disc list-inside text-gray-200">
              <li className='text-lg'>Interfaz intuitiva para navegar por mis habilidades y proyectos.</li>
              <li className='text-lg'>Secciones dedicadas a proyectos, habilidades, formación y contacto.</li>
              <li className='text-lg'>Diseño responsivo para una experiencia fluida en dispositivos móviles y de escritorio.</li>
              <li className='text-lg'>Integración con GitHub para mostrar repositorios y código fuente.</li>
              <li className='text-lg'>Formulario de contacto funcional.</li>
            </ul>
            <h3 className="text-xl font-semibold mt-8 mb-2">Tecnologías Utilizadas:</h3>
            <ul className="list-disc list-inside text-gray-200">
              <li className='text-lg'>Frontend: React, Tailwind CSS.</li>
              <li className='text-lg'>Herramientas: Visual Studio Code, GitHub, Figma para prototipos.</li>
            </ul>

            <div className="mt-12 text-center">
              <h2 className="text-2xl font-semibold mb-4 inline-block border-b-4">Identidad Visual</h2>

              <div className="mb-8">
                <h3 className="text-xl font-semibold mb-2">Página Principal</h3>
                <p className="text-lg text-gray-200">
                  La página principal incluye una introducción breve sobre mí y botones de navegación hacia las secciones de proyectos y habilidades.
                </p>
                <img src={portafolioHome} alt="Página Principal" className="mb-4 w-full mt-3" />
              </div>

              <div className="mb-8 text-center">
                <h3 className="text-xl font-semibold mb-2">Sección de Habilidades</h3>
                <p className="text-lg text-gray-200">
                  Muestra mis habilidades técnicas con iconos representativos y breves descripciones.
                </p>
                <img src={portafolioSkills} alt="Sección de Habilidades" className="mb-4 w-full mt-3" />
              </div>

              <div className="mb-8 text-center">
                <h3 className="text-xl font-semibold mb-2">Sección de Proyectos</h3>
                <p className="text-lg text-gray-200">
                  Incluye un resumen de mis proyectos con enlaces a más detalles y repositorios en GitHub.
                </p>
                <img src={portafolioProjects} alt="Sección de Proyectos" className="mb-4 w-full mt-3" />
              </div>

              <div className="mb-8 text-center">
                <h3 className="text-xl font-semibold mb-2">Sección de Contacto</h3>
                <p className="text-lg text-gray-200">
                  Formulario de contacto para que los empleadores o interesados puedan comunicarse fácilmente.
                </p>
                <img src={portafolioContact} alt="Sección de Contacto" className="mb-4 w-full mt-3" />
              </div>

              <div className="mb-8 text-center">
                <h3 className="text-xl font-semibold mb-2">Pie de Página</h3>
                <p className="text-lg text-gray-200">
                  Contiene enlaces a mis redes sociales, información de copyright y navegación adicional.
                </p>
                <img src={portafolioFooter} alt="Pie de Página" className="mb-4 w-full mt-3" />
              </div>
            </div>

            <div className="mt-12 text-center">
              <h3 className="text-xl font-semibold mb-2">Documentación y Guía de Estilos</h3>
              <div className="mt-4 flex justify-center">
                <button
                  className="bg-white text-black font-bold py-2 px-4 mr-4 rounded"
                  onClick={descargarDocumentacion}
                >
                  Descargar Documentación
                </button>
                <button
                  className="bg-white text-black font-bold py-2 px-4 rounded"
                  onClick={descargarGuiaEstilo}
                >
                  Descargar Guía de Estilo
                </button>
              </div>
            </div>

          </div>
        </div>
      </Transition>
    </>
  );
}

export default ProyectoDetalle2;
