import { Transition } from '@headlessui/react';
import React from 'react';
import street from '../image/street.jpg';

function ProyectoDetalle1({ seccionAnimacion }) {
  return (
    <Transition
      show={seccionAnimacion === 'Proyecto1'}
      enter="transition ease-out duration-500"
      enterFrom="opacity-0 transform translate-y-full"
      enterTo="opacity-100 transform translate-y-0"
      leave="transition ease-in duration-300"
      leaveFrom="opacity-100 transform translate-y-0"
      leaveTo="opacity-0 transform translate-y-full"
    >
      <div
        id="Proyecto1"
        className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${street})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        {/* Contenido del detalle del proyecto */}
        <h1 className="text-4xl font-bold">Detalles del Proyecto 1</h1>
        <p className="text-lg">Descripción del Proyecto 1...</p>
        {/* Agregar cualquier otro contenido que necesites */}
      </div>
    </Transition>
  );
}

export default ProyectoDetalle1;
