import React, { useEffect, useRef } from 'react';

function Slider() {
  const navegacion = (seccion) => {
    document.getElementById(seccion).scrollIntoView({ behavior: 'smooth' });
  };

  // Referencia a los slides
  const inicioRef = useRef(null);
  const tecnologiasRef = useRef(null);
  const experienciaRef = useRef(null);
  const proyectosRef = useRef(null);

  // Función para manejar el desplazamiento
  const handleScroll = () => {
    const scrollPosition = window.scrollY;

    // Determinar qué sección está visible
    if (scrollPosition >= proyectosRef.current.offsetTop) {
      highlightNavItem('Proyectos');
    } else if (scrollPosition >= experienciaRef.current.offsetTop) {
      highlightNavItem('ExperienciayFormacion');
    } else if (scrollPosition >= tecnologiasRef.current.offsetTop) {
      highlightNavItem('Tecnologias');
    } else {
      highlightNavItem('Inicio');
    }
  };

  // Función para resaltar el botón de navegación activo
  const highlightNavItem = (seccion) => {
    const navButtons = document.querySelectorAll('.nav-button');

    navButtons.forEach((button) => {
      if (button.textContent === seccion) {
        button.classList.add('bg-gray-200', 'text-gray-800');
      } else {
        button.classList.remove('bg-gray-200', 'text-gray-800');
      }
    });
  };

  // Agregar evento de scroll
  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="relative">
      {/* Navegación */}
      <nav className="fixed top-0 left-0 right-0 flex justify-center items-center py-4 z-50">
        <div className="ml-96 mr-96 flex justify-center items-center rounded-full shadow-md bg-white">
          <button
            onClick={() => navegacion('Inicio')}
            className="nav-button hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full"
          >
            Inicio
          </button>
          <button
            onClick={() => navegacion('Tecnologias')}
            className="nav-button hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4"
          >
            Tecnologías
          </button>
          <button
            onClick={() => navegacion('ExperienciayFormacion')}
            className="nav-button hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4"
          >
            Experiencia y Formación
          </button>
          <button
            onClick={() => navegacion('Proyectos')}
            className="nav-button hover:bg-gray-200 hover:text-gray-800 py-2 px-4 focus:outline-none rounded-full ml-4"
          >
            Proyectos
          </button>
        </div>
      </nav>

      {/* Slides */}
      <div className="relative">
        <div ref={inicioRef} id="Inicio" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-green-200 to-blue-200">
          <h2 className="text-4xl font-bold">The First slide</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
        <div ref={tecnologiasRef} id="Tecnologias" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white">
          <h2 className="text-4xl font-bold">The Second slide</h2>
          <p className="mt-2">Scroll Down for next slide</p>
        </div>
        <div ref={experienciaRef} id="ExperienciayFormacion" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-purple-800 to-pink-800 text-white">
          <h2 className="text-4xl font-bold">The Third slide</h2>
          <p className="mt-2">Scroll Down</p>
        </div>
        <div ref={proyectosRef} id="Proyectos" className="sticky top-0 h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-200 to-indigo-100 text-black">
          <h2 className="text-4xl font-bold">The Fourth slide</h2>
        </div>
      </div>
    </div>
  );
}

export default Slider;
