import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import Tecnologias from './Tecnologias';
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuAbierto, setMenuAbierto] = useState(false);

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearInterval(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
    setMenuAbierto(false);
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto',
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <Transition.Child
            as="div" // Especifica que Transition.Child renderice un <div>
            className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50"
          >
            <EfectoEscritura />
          </Transition.Child>
        </Transition>

        <Transition
          show={!mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <nav className="fixed top-0 left-0 right-0 flex justify-center items-center py-4 z-50 bg-gray-800">
            <div className="ml-96 mr-96 flex justify-center items-center rounded-full shadow-md bg-white">
              <button
                onClick={() => navegacion('Inicio')}
                className={`py-5 pr-10 pl-10 px-6 focus:outline-none rounded-full transition duration-300 ease-in-out ${
                  seccionAnimacion === 'Inicio' ? 'text-black font-bold' : 'text-gray-400 font-bold'
                }`}
              >
                INICIO
              </button>
              <button
                onClick={() => navegacion('Tecnologias')}
                className={`py-5 px-6 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                  seccionAnimacion === 'Tecnologias' ? 'text-black font-bold' : 'text-gray-400 font-bold'
                }`}
              >
                TECNOLOGÍAS
              </button>
              <button
                onClick={() => navegacion('ExperienciayFormacion')}
                className={`py-5 px-4 pr-10 pl-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                  seccionAnimacion === 'ExperienciayFormacion' ? 'text-black font-bold' : 'text-gray-400 font-bold'
                }`}
              >
                EXPERIENCIA Y FORMACIÓN
              </button>
              <button
                onClick={() => navegacion('Proyectos')}
                className={`py-5 pl-10 px-10 pr-10 focus:outline-none rounded-full ml-4 transition duration-300 ease-in-out ${
                  seccionAnimacion === 'Proyectos' ? 'text-black font-bold' : 'text-gray-400 font-bold'
                }`}
              >
                PROYECTOS
              </button>
            </div>
          </nav>

          <nav className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 sm:hidden z-50 bg-gray-800">
            <div className="ml-4">
              <button className="text-gray-400 focus:outline-none" onClick={() => setMenuAbierto(!menuAbierto)}>
                <svg
                  className="w-8 h-8"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
                </svg>
              </button>
            </div>
            <Transition
              show={menuAbierto}
              enter="transition-opacity duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity duration-300"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <Transition.Child
                as="div" // Especifica que Transition.Child renderice un <div>
                className="absolute inset-0 bg-white opacity-95 z-50 flex flex-col items-center justify-center"
              >
                <button
                  onClick={() => {
                    navegacion('Inicio');
                    setMenuAbierto(false);
                  }}
                  className={`py-2 px-4 text-gray-800 font-bold ${seccionAnimacion === 'Inicio' ? 'bg-gray-200' : ''}`}
                >
                  INICIO
                </button>
                <button
                  onClick={() => {
                    navegacion('Tecnologias');
                    setMenuAbierto(false);
                  }}
                  className={`py-2 px-4 text-gray-800 font-bold ${seccionAnimacion === 'Tecnologias' ? 'bg-gray-200' : ''}`}
                >
                  TECNOLOGÍAS
                </button>
                <button
                  onClick={() => {
                    navegacion('ExperienciayFormacion');
                    setMenuAbierto(false);
                  }}
                  className={`py-2 px-4 text-gray-800 font-bold ${
                    seccionAnimacion === 'ExperienciayFormacion' ? 'bg-gray-200' : ''
                  }`}
                >
                  EXPERIENCIA Y FORMACIÓN
                </button>
                <button
                  onClick={() => {
                    navegacion('Proyectos');
                    setMenuAbierto(false);
                  }}
                  className={`py-2 px-4 text-gray-800 font-bold ${seccionAnimacion === 'Proyectos' ? 'bg-gray-200' : ''}`}
                >
                  PROYECTOS
                </button>
              </Transition.Child>
            </Transition>
          </nav>
        </Transition>

        <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
        <Tecnologias seccionAnimacion={seccionAnimacion} />
        <Experiencia seccionAnimacion={seccionAnimacion} />
        <Proyectos seccionAnimacion={seccionAnimacion} />
        <Contacto seccionAnimacion={seccionAnimacion} />
      </div>
    </>
  );
}

export default Slider;
