import React from 'react'

function Nav() {

    const navegacion = (seccion) => {
        document.getElementById(seccion).scrollIntoView({ behavior : 'smooth'});
    };

  return (
    <>
    <nav className="flex justify-center items-center rounded-full bg-gray-200 shadow-md">
        <button onClick={() => navegacion('Inicio')} className="hover:bg-gray-300 py-2 px-4 focus:outline-none rounded-full">
            Inicio
        </button>
        <button onClick={() => navegacion('Tecnologias')} className="hover:bg-gray-300 py-2 px-4 focus:outline-none rounded-full">
            Tecnologías
        </button>
        <button onClick={() => navegacion('ExperienciayFormacion')} className="hover:bg-gray-300 py-2 px-4 focus:outline-none rounded-full">
            Experiencia y Formación
        </button>
        <button onClick={() => navegacion('Proyectos')} className="hover:bg-gray-300 py-2 px-4 focus:outline-none rounded-full">
            Proyectos
        </button>
    </nav>
    </>
  )
}

export default Nav
