import React, { useEffect, useState } from 'react';
import { Transition } from '@headlessui/react';
import EfectoEscritura from './EfectoEscritura';
import Inicio from './Inicio';
import InicioMovil from './InicioMovil';
import Tecnologias from './Tecnologias';
import TecnologiasMovil from './TecnologiasMovil'; // Importar TecnologiasMovil
import Experiencia from './Experiencia';
import Proyectos from './Proyectos';
import Contacto from './Contacto';

function Slider() {
  const [seccionAnimacion, setSeccionAnimacion] = useState('Inicio');
  const [seccionAnimacionMovil, setSeccionAnimacionMovil] = useState('Inicio'); // Estado para sección en móvil
  const [mostrarPantallaInicial, setMostrarPantallaInicial] = useState(true);
  const [menuMobileOpen, setMenuMobileOpen] = useState(false);

  useEffect(() => {
    const tiempo = setTimeout(() => {
      setMostrarPantallaInicial(false);
    }, 2250);

    return () => clearTimeout(tiempo);
  }, []);

  const navegacion = (seccion) => {
    setSeccionAnimacion(seccion);
    setSeccionAnimacionMovil(seccion); // Actualizar la sección en móvil al navegar
    const elemento = document.getElementById(seccion);
    if (elemento) {
      elemento.scrollIntoView({ behavior: 'smooth' });
    }
    setMenuMobileOpen(false); // Cerrar el menú hamburguesa al hacer clic en un enlace
  };

  useEffect(() => {
    const titulos = {
      Inicio: 'Inicio',
      Tecnologias: 'Tecnología',
      ExperienciayFormacion: 'Experiencia y Formación',
      Proyectos: 'Proyectos',
      Contacto: 'Contacto'
    };
    document.title = titulos[seccionAnimacion];
  }, [seccionAnimacion]);

  return (
    <>
      <div className="relative">
        <Transition
          show={mostrarPantallaInicial}
          enter="transition-opacity duration-1000"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="transition-opacity duration-1000"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black flex items-center justify-center text-white text-4xl font-bold z-50">
            <EfectoEscritura />
          </div>
        </Transition>

        {/* Menú hamburguesa para móvil */}
        <div className="fixed top-0 left-0 right-0 flex justify-between items-center py-4 px-6 z-50 md:hidden">
          <button
            onClick={() => setMenuMobileOpen(!menuMobileOpen)}
            className="text-gray-500 focus:outline-none"
            style={{ marginBottom: '1rem' }}
          >
            <svg
              className="w-6 h-6 text-white"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              {menuMobileOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M4 6h16M4 12h16m-7 6h7"
                />
              )}
            </svg>
          </button>
          <div className={`absolute top-full left-0 right-0 bg-white shadow-md rounded-b-lg overflow-hidden transition-all duration-300 ease-in-out ${menuMobileOpen ? 'opacity-100 visible' : 'opacity-0 invisible'}`}>
            <button
              onClick={() => navegacion('Inicio')}
              className={`block py-3 px-6 text-gray-800 font-semibold hover:bg-gray-200 w-full text-left ${seccionAnimacion === 'Inicio' ? 'bg-gray-200' : ''}`}
            >
              Inicio
            </button>
            <button
              onClick={() => navegacion('Tecnologias')}
              className={`block py-3 px-6 text-gray-800 font-semibold hover:bg-gray-200 w-full text-left ${seccionAnimacion === 'Tecnologias' ? 'bg-gray-200' : ''}`}
            >
              Tecnología
            </button>
            <button
              onClick={() => navegacion('ExperienciayFormacion')}
              className={`block py-3 px-6 text-gray-800 font-semibold hover:bg-gray-200 w-full text-left ${seccionAnimacion === 'ExperienciayFormacion' ? 'bg-gray-200' : ''}`}
            >
              Experiencia y Formación
            </button>
            <button
              onClick={() => navegacion('Proyectos')}
              className={`block py-3 px-6 text-gray-800 font-semibold hover:bg-gray-200 w-full text-left ${seccionAnimacion === 'Proyectos' ? 'bg-gray-200' : ''}`}
            >
              Proyectos
            </button>
            <button
              onClick={() => navegacion('Contacto')}
              className={`block py-3 px-6 text-gray-800 font-semibold hover:bg-gray-200 w-full text-left ${seccionAnimacion === 'Contacto' ? 'bg-gray-200' : ''}`}
            >
              Contacto
            </button>
          </div>
        </div>

        {/* Contenido */}
        <div className="overflow-hidden">
          <Inicio seccionAnimacion={seccionAnimacion} navegacion={navegacion} />
          <InicioMovil seccionAnimacionMovil={seccionAnimacionMovil} navegacion={navegacion} />
          <Tecnologias seccionAnimacion={seccionAnimacion} />
          <TecnologiasMovil seccionAnimacionMovil={seccionAnimacionMovil} />
          <Experiencia seccionAnimacion={seccionAnimacion} />
          <Proyectos seccionAnimacion={seccionAnimacion} />
          <Contacto seccionAnimacion={seccionAnimacion} />
        </div>
      </div>
    </>
  );
}

export default Slider;
